import React from "react";
import classes from './Block6.module.css';

class Block6 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displatStatus: false,
            imgSrc: ''
        };

        this.clickHandle = this.clickHandle.bind(this);
    }

        clickHandle(e){
            this.setState({displatStatus:true});
            console.log(e.target.value());
        //this.setState({imgSrc:e.target});
    }

    render() {
    return(

            <div className={classes.block}>
            <h2>portfolio</h2>
            <div className={classes.blockFlex}>
            {this.props.homeBlock6.map(e =>
                <div key={e.id} className={classes.item}>
                    <img src={e.img} alt={e.alt} onClick={this.clickHandle(e)}/>
                    <div className={classes.blockAnimation}>{e.alt}</div>
                </div>
            )}
            </div>
            <a href="/portfolio"> <button className={classes.btn}>check out other project &#8594; </button></a>
            <div className={classes.hide} style={(this.stait.displatStatus) ? {display: "block"} : {display: "none"}}>
            <img src="" alt=""/>

            </div>
            </div>






    );
    }
};
export default Block6;

