import s from './Gallery.module.css'
import React, {Component} from "react";


class Slider1 extends  React.Component{

    constructor() {
        super();
        this.state = {
            image:[],
            currentImageIndex: 0,
            isCycleMode: false
        };
        this.nextSliderHandler = this.nextSliderHandler.bind(this);
    }
    nextSliderHandler (e){
        let newIndex = this.state.currentImageIndex;
        if(e.currentImageIndex.dataset.direction ==='next') {
           newIndex = this.state.currentImageIndex + 1;
        }else{
             newIndex = this.state.currentImageIndex -1;
        }

            this.setState({currentImageIndex: newIndex});
     }



    render(){
        return(

                <div className={s.blockGallery}>

                    <div className={s.container}>
                    <button data-direction='prev' onClick={this.nextSliderHandler.bind(this)} className={s.btn}> prev </button>
                    {this.state.currentImageIndex}
                    <button data-direction='next' onClick={this.nextSliderHandler.bind(this)} className={s.btn}> next</button>




                </div>
                </div>
        );

    }



}
export default Slider1;
