import s from './Gallery.module.css'
import React, {Component} from "react";


class Slider1 extends  React.Component{

    constructor(props) {
        console.log(props.galleryBlue)
        super(props);
        this.state = {
            image: props.galleryBlue,
            currentImageIndex: 0,
            isCycleMode: false
        };
        this.nextSliderHandler = this.nextSliderHandler.bind(this);
    }
    nextSliderHandler (e){
        let newIndex = this.state.currentImageIndex;
        if(e.currentTarget.dataset.direction ==='next') {
            if(newIndex < this.state.image.length-1){
                newIndex = this.state.currentImageIndex + 1;
            }

        }else{
            if(newIndex > 0){
                newIndex = this.state.currentImageIndex -1;
            }

        }

            this.setState({currentImageIndex: newIndex});
     }



    render(){
        return(

                <div className={s.blockGallery}>

                    <div className={s.container}>
                        <img src={this.state.image[this.state.currentImageIndex]} alt=""/>
                    <button data-direction='prev' onClick={this.nextSliderHandler.bind(this)} className={s.prev}> prev </button>
                    <button data-direction='next' onClick={this.nextSliderHandler.bind(this)} className={s.next}> next</button>
                </div>

                </div>
        );

    }



}
export default Slider1;
