import React from 'react';
import {NavLink} from "react-router-dom";
import classes from './Animation.module.css'






let WebAnimation=(props) =>{
    console.log(props);
let img1 = props.animation[0].img1;
let text1 = props.animation[0].text1;
let text2 = props.animation[0].text2;
let img2 = props.animation[0].img2;
let quote = props.animation[0].quote;

    return(
            <div className={classes.block}>
                <div className={classes.page}>
                    <h3>Standardization between design tools</h3>
                    <a href="#comment">Live a comment /</a><a href="/blog">uncategorized</a>
                    <div className={classes.foto1}>
                        <img src={img1} alt="foto"/>
                    </div>
                    <div className={classes.text}> {text1}</div>
                    <div className={classes.quote}> {quote}</div>
                    <div className={classes.foto2}>
                        <img src={img2} alt=""/>
                        <div className={classes.info}>

                        </div>
                    </div>
                    <div className={classes.columns}> {text2} </div>
                    <div className={classes.nav}>
                        <button className={classes.prev}><NavLink to='#'> &#8592; previous post</NavLink> </button>
                        <button className={classes.next}> <NavLink to ='/standardization'>next post &#8594;</NavLink></button>
                    </div>
                    <div id='comment' className={classes.comment}>
                        <span>Leave a Comment</span> <br/>
                        <span>Your email address will not be published.</span>
                        <form action="">
                            <textarea type="textarea" id='textarea' placeholder='Type here...'/>
                            <input type="text" id='name' placeholder='Name*' required/>
                            <input type="text" id ='email' placeholder='Email*' required/>
                            <input type="text" id = 'text' placeholder='Website'/>
                            <input type="checkbox" id = 'check'/>
                            <label htmlFor="check"> Save my name, email, and website in this browser for the next time I comment.</label>



                        </form>
                    </div>
                </div>
            </div>
    );


};




export default WebAnimation;
